import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Quiz = () => {
  const { lessonId } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState({});
  const [score, setScore] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/quizzes/${lessonId}`)
      .then(res => setQuiz(res.data))
      .catch(() => setQuiz(null));
  }, [lessonId]);

  if (!quiz) return <div>No quiz available for this lesson.</div>;

  const handleAnswer = (qIndex, optionIndex) => {
    setAnswers(prev => ({ ...prev, [qIndex]: optionIndex }));
  };

  const handleSubmit = () => {
    let correctCount = 0;
    quiz.questions.forEach((q, i) => {
      if (answers[i] === q.correctAnswerIndex) correctCount++;
    });
    setScore(correctCount);

    // Here you can call API to save progress & score to backend for this user...
    alert(`You scored ${correctCount} out of ${quiz.questions.length}`);
    navigate('/');
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Quiz</h2>
      {quiz.questions.map((q, i) => (
        <div key={i} style={{ marginBottom: 20 }}>
          <p>{i + 1}. {q.questionText}</p>
          {q.options.map((opt, idx) => (
            <div key={idx}>
              <input
                type="radio"
                id={`q${i}opt${idx}`}
                name={`question${i}`}
                onChange={() => handleAnswer(i, idx)}
                checked={answers[i] === idx}
              />
              <label htmlFor={`q${i}opt${idx}`}>{opt}</label>
            </div>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit} disabled={Object.keys(answers).length !== quiz.questions.length}>
        Submit
      </button>
      {score !== null && <p>Your score: {score} / {quiz.questions.length}</p>}
    </div>
  );
};

export default Quiz;
