import React, { useState } from "react";
import './App.css';

const Quiz = () => {
  const questions = [
    {
      question: "What is React?",
      options: ["Library", "Framework", "Database"],
      answer: "Library",
    },
    {
      question: "What is JSX?",
      options: ["JavaScript XML", "JSON", "HTML"],
      answer: "JavaScript XML",
    },
  ];

  const [index, setIndex] = useState(0);
  const [score, setScore] = useState(0);

  const handleAnswer = (option) => {
    if (option === questions[index].answer) setScore(score + 1);
    if (index + 1 < questions.length) setIndex(index + 1);
    else alert(`Quiz complete! Your score: ${score + 1}`);
  };

  return (
    <div>
      <h3>{questions[index].question}</h3>
      {questions[index].options.map((opt) => (
        <button key={opt} onClick={() => handleAnswer(opt)}>{opt}</button>
      ))}
    </div>
  );
};

export default Quiz;
