import React, { useState } from "react";
import { motion } from "framer-motion";
import { FaDownload, FaEye } from "react-icons/fa";
import '../App.css';

const resources = [
  {
    id: 1,
    title: "C++ Basics Notes",
    subject: "Programming",
    author: "John Doe",
    size: "1.2 MB",
    url: "https://example.com/cpp-notes.pdf",
  },
  {
    id: 2,
    title: "Machine Learning Cheatsheet",
    subject: "AI/ML",
    author: "Jane Smith",
    size: "900 KB",
    url: "https://example.com/ml-cheatsheet.pdf",
  },
  // Add more resources here
];

const ResourceLibrary = () => {
  const [filter, setFilter] = useState({ subject: "", author: "" });

  const filteredResources = resources.filter(
    (res) =>
      (filter.subject === "" || res.subject === filter.subject) &&
      (filter.author === "" || res.author === filter.author)
  );

  return (
    <motion.div
      className="resource-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <h1 className="title">ðŸ“š E-Book / PDF Resource Library</h1>

      <div className="filters">
        <select onChange={(e) => setFilter({ ...filter, subject: e.target.value })}>
          <option value="">Filter by Subject</option>
          <option value="Programming">Programming</option>
          <option value="AI/ML">AI/ML</option>
        </select>

        <select onChange={(e) => setFilter({ ...filter, author: e.target.value })}>
          <option value="">Filter by Author</option>
          <option value="John Doe">John Doe</option>
          <option value="Jane Smith">Jane Smith</option>
        </select>
      </div>

      <div className="resource-list">
        {filteredResources.map((res) => (
          <motion.div
            className="resource-card"
            key={res.id}
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <h3>{res.title}</h3>
            <p><strong>Subject:</strong> {res.subject}</p>
            <p><strong>Author:</strong> {res.author}</p>
            <p><strong>Size:</strong> {res.size}</p>
            <div className="actions">
              <a href={res.url} target="_blank" rel="noreferrer" className="btn preview">
                <FaEye /> Preview
              </a>
              <a href={res.url} download className="btn download">
                <FaDownload /> Download
              </a>
            </div>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default ResourceLibrary;
