import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './Timetable.css';
import { v4 as uuidv4 } from 'uuid';
import { parseISO } from "date-fns";

const locales = {
  'en-US': require('date-fns/locale/en-US'),
};

const localizer = momentLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales
});

const Timetable = () => {
  const [events, setEvents] = useState([]);

  const handleSelect = ({ start, end }) => {
    const title = window.prompt('Enter schedule title:');
    if (title) {
      const newEvent = {
        id: uuidv4(),
        title,
        start,
        end,
      };
      setEvents([...events, newEvent]);
    }
  };

  const handleEventClick = (event) => {
    if (window.confirm(`Remove "${event.title}"?`)) {
      setEvents(events.filter((e) => e.id !== event.id));
    }
  };

  return (
    <div className="timetable-container">
      <h2 className="timetable-heading">ðŸ“… Weekly Study Calendar</h2>
      <Calendar
        selectable
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 600, margin: "40px auto", width: "90%" }}
        onSelectSlot={handleSelect}
        onSelectEvent={handleEventClick}
        views={['week', 'day']}
      />
      <p className="instructions">Click and drag to add. Click event to remove.</p>
    </div>
  );
};

export default Timetable;
