import React, { useState } from "react";
import moment from "moment";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { v4 as uuidv4 } from "uuid";

import "./Timetable.css";

const localizer = momentLocalizer(moment);

const Timetable = () => {
  const [events, setEvents] = useState([]);

  const handleSelect = ({ start, end }) => {
    const title = window.prompt("Enter schedule title:");
    if (title && title.trim()) {
      setEvents([...events, { id: uuidv4(), start, end, title: title.trim() }]);
    } else {
      alert("‚ö†Ô∏è A title is required!");
    }
  };

  const handleEventClick = (event) => {
    if (window.confirm(`Delete "${event.title}"?`)) {
      setEvents((prev) => prev.filter((e) => e.id !== event.id));
    }
  };

  return (
    <div className="timetable-container">
      <h2 className="timetable-heading">üìÖ Weekly Study Calendar</h2>
      
      {console.log("events state:", events)}

      <Calendar
        localizer={localizer}
        events={events.filter(e => e && e.title && e.start && e.end)}  // ‚úÖ Sanitization
        startAccessor="start"
        endAccessor="end"
        selectable
        onSelectSlot={handleSelect}
        onSelectEvent={handleEventClick}
        views={["week", "day"]}
        style={{ height: 600, margin: "30px auto", width: "90%" }}
      />

      <p className="instructions">
        üñ±Ô∏è Click and drag to add an event. üóëÔ∏è Click on an event to delete.
      </p>
    </div>
  );
};

export default Timetable;
