// src/pages/Quiz.js
import React, { useState, useEffect } from "react";
import "./Quiz.css";

const quizData = {
  "Web Development": [
    {
      question: "What does HTML stand for?",
      options: ["Hyper Trainer Marking Language", "HyperText Markup Language", "HyperText Markdown Language", "None of the above"],
      answer: "HyperText Markup Language",
    },
    {
      question: "Which tag is used for linking CSS?",
      options: ["<style>", "<link>", "<script>", "<css>"],
      answer: "<link>",
    },
    // Add 8 more questions for Web Development...
  ],
  "Machine Learning": [
  {
    "question": "What is the primary goal of supervised learning?",
    "options": ["Clustering data", "Predicting outputs from labeled data", "Finding patterns in unlabeled data", "None of the above"],
    "answer": "Predicting outputs from labeled data"
  },
  {
    "question": "Which algorithm is used for splitting nodes in Decision Trees?",
    "options": ["Gini Impurity", "Euclidean Distance", "Cosine Similarity", "Pearson Coefficient"],
    "answer": "Gini Impurity"
  },
  {
    "question": "What does 'CNN' stand for in deep learning?",
    "options": ["Common Neural Network", "Convolutional Neural Network", "Complex Neural Network", "Cumulative Neural Network"],
    "answer": "Convolutional Neural Network"
  },
  {
    "question": "Which library is commonly used for ML in Python?",
    "options": ["NumPy", "Pandas", "Scikit-learn", "Matplotlib"],
    "answer": "Scikit-learn"
  },
  {
    "question": "What is 'overfitting' in ML?",
    "options": ["Model performs well on training data but poorly on test data", "Model is too simple", "Model ignores noise", "None of the above"],
    "answer": "Model performs well on training data but poorly on test data"
  },
  {
    "question": "Which technique reduces overfitting by adding random noise?",
    "options": ["Dropout", "Batch Normalization", "Data Augmentation", "Gradient Descent"],
    "answer": "Dropout"
  },
  {
    "question": "What is the role of a 'loss function'?",
    "options": ["Measure model accuracy", "Quantify model error", "Optimize hyperparameters", "Visualize data"],
    "answer": "Quantify model error"
  },
  {
    "question": "Which ML type does K-Means belong to?",
    "options": ["Supervised", "Unsupervised", "Reinforcement", "Semi-supervised"],
    "answer": "Unsupervised"
  },
  {
    "question": "What is 'feature engineering'?",
    "options": ["Selecting important features", "Creating new features from raw data", "Removing all features", "Plotting features"],
    "answer": "Creating new features from raw data"
  },
  {
    "question": "Which evaluation metric is used for classification?",
    "options": ["Mean Squared Error", "R2 Score", "Accuracy", "Silhouette Score"],
    "answer": "Accuracy"
  }
],
 "Cybersecurity": [
  {
    question: "What is a 'zero-day' vulnerability?",
    options: ["A bug fixed within a day", "An unknown exploit with no patch", "A low-risk flaw", "A hardware failure"],
    answer: "An unknown exploit with no patch"
  },
  {
    question: "Which protocol provides secure communication over a network?",
    options: ["HTTP", "FTP", "HTTPS", "SMTP"],
    answer: "HTTPS"
  },
  {
    question: "What is 'phishing'?",
    options: ["A fishing technique", "A type of malware", "Fraudulent attempt to steal sensitive data", "A DDoS attack"],
    answer: "Fraudulent attempt to steal sensitive data"
  },
  {
    question: "Which tool is used for network scanning?",
    options: ["Wireshark", "Nmap", "Metasploit", "Burp Suite"],
    answer: "Nmap"
  },
  {
    question: "What does 'VPN' stand for?",
    options: ["Virtual Private Network", "Visual Private Node", "Verified Public Network", "Virtual Public Node"],
    answer: "Virtual Private Network"
  },
  {
    question: "Which attack involves overwhelming a server with traffic?",
    options: ["Phishing", "SQL Injection", "DDoS", "Man-in-the-Middle"],
    answer: "DDoS"
  },
  {
    question: "What is 'two-factor authentication'?",
    options: ["Using two passwords", "Verifying identity via two methods", "Double encryption", "None of the above"],
    answer: "Verifying identity via two methods"
  },
  {
    question: "Which encryption is asymmetric?",
    options: ["AES", "RSA", "DES", "Blowfish"],
    answer: "RSA"
  },
  {
    question: "What is the purpose of a firewall?",
    options: ["Block unauthorized access", "Speed up internet", "Store backups", "Encrypt files"],
    answer: "Block unauthorized access"
  },
  {
    question: "Which malware replicates itself?",
    options: ["Spyware", "Worm", "Adware", "Ransomware"],
    answer: "Worm"
  }
],
"React": [
  {
    question: "What is React?",
    options: ["A programming language", "A JavaScript library for building UIs", "A database", "A CSS framework"],
    answer: "A JavaScript library for building UIs"
  },
  {
    question: "Which hook is used for side effects?",
    options: ["useState", "useEffect", "useContext", "useReducer"],
    answer: "useEffect"
  },
  {
    question: "What is JSX?",
    options: ["A syntax extension for JavaScript", "A state management tool", "A testing library", "A build tool"],
    answer: "A syntax extension for JavaScript"
  },
  {
    question: "How do you pass data to child components?",
    options: ["useState", "props", "useRef", "contextAPI"],
    answer: "props"
  },
  {
    question: "What is the virtual DOM?",
    options: ["A lightweight copy of the real DOM", "A database", "A browser API", "A security feature"],
    answer: "A lightweight copy of the real DOM"
  },
  {
    question: "Which command creates a React app?",
    options: ["npm create-react-app", "npx create-react-app", "react init", "npm build-react"],
    answer: "npx create-react-app"
  },
  {
    question: "What is Redux used for?",
    options: ["Styling", "State management", "Routing", "API calls"],
    answer: "State management"
  },
  {
    question: "Which lifecycle method runs after rendering?",
    options: ["componentDidMount", "componentWillUnmount", "render", "constructor"],
    answer: "componentDidMount"
  },
  {
    question: "How do you handle forms in React?",
    options: ["useForm", "Formik", "useState", "All of the above"],
    answer: "All of the above"
  },
  {
    question: "What is React Router used for?",
    options: ["State management", "Navigation", "Styling", "API integration"],
    answer: "Navigation"
  }
],
};

const Quiz = () => {
  const [topic, setTopic] = useState("Web Development");
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [timer, setTimer] = useState(30);
  const [showScore, setShowScore] = useState(false);

  const questions = quizData[topic];

  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prev) => {
        if (prev === 1) {
          handleNext();
          return 30;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(countdown);
  }, [currentQuestion, topic]);

  const handleNext = () => {
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 1);
    }
    setSelectedOption("");
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
      setTimer(30);
    } else {
      setShowScore(true);
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setScore(0);
    setSelectedOption("");
    setShowScore(false);
    setTimer(30);
  };

  return (
    <div className="quiz-container">
      <h2>üß† {topic} Quiz</h2>

      <div className="topic-select">
        <label>Select Topic: </label>
        <select onChange={(e) => {
          setTopic(e.target.value);
          handleRestart();
        }} value={topic}>
          {Object.keys(quizData).map((t) => (
            <option key={t} value={t}>{t}</option>
          ))}
        </select>
      </div>

      {showScore ? (
        <div className="result-box">
          <h3>Your Score: {score} / {questions.length}</h3>
          <button onClick={handleRestart}>Restart</button>
        </div>
      ) : (
        <div className="question-box">
          <div className="timer">‚è±Ô∏è {timer}s</div>
          <h4>{questions[currentQuestion].question}</h4>
          <ul>
            {questions[currentQuestion].options.map((opt, index) => (
              <li
                key={index}
                className={selectedOption === opt ? "selected" : ""}
                onClick={() => setSelectedOption(opt)}
              >
                {opt}
              </li>
            ))}
          </ul>
          <button onClick={handleNext}>Next</button>
        </div>
      )}
    </div>
  );
};

export default Quiz;
