import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { v4 as uuidv4 } from "uuid";

const localizer = momentLocalizer(moment);

const Timetable = () => {
  const [events, setEvents] = useState([]);

  const handleSelect = ({ start, end }) => {
    const title = window.prompt("Enter schedule title:");
    if (title && title.trim()) {
      const newEvent = {
        id: uuidv4(),
        title: title.trim(),
        start,
        end,
      };
      setEvents((prevEvents) => [...prevEvents, newEvent]);
    } else {
      alert("‚ö†Ô∏è Title cannot be empty!");
    }
  };

  const handleEventClick = (event) => {
    const confirmDelete = window.confirm(`Delete event: "${event.title}"?`);
    if (confirmDelete) {
      setEvents((prevEvents) => prevEvents.filter((e) => e.id !== event.id));
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        üìÖ Weekly Study Calendar
      </h2>

      <Calendar
        localizer={localizer}
        events={events.filter(
          (e) => e && e.title && e.start && e.end
        )} // ‚úÖ Filter invalid events
        startAccessor="start"
        endAccessor="end"
        selectable
        onSelectSlot={handleSelect}
        onSelectEvent={handleEventClick}
        views={["week", "day"]}
        style={{ height: 600, width: "90%", margin: "auto" }}
      />

      <p style={{ textAlign: "center", marginTop: "1rem", color: "#777" }}>
        üñ±Ô∏è Click and drag to add a schedule. üî¥ Click an event to delete.
      </p>
    </div>
  );
};

export default Timetable;
